package days03;

public class Ex07_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// [비교연산자]
		int i =10, j = 3;
		
		System.out.println(i > j); // true
		System.out.println(i < j); // false
		System.out.println(i >= j); // true
		System.out.println(i <= j); // false
		
		// i  j 같다
//		System.out.println(i = j); // 3
		System.out.println(i == j); // false
		System.out.println(i != j); // true
		

	}

}
